export const categories = [dyr, matur, farartaeki, manneskja, stadir, klaednadur];

/**
 * Look up a category by its id string.
 * @param {string} id - The category id (e.g. 'dyr', 'matur', 'farartaeki', 'manneskja', 'stadir', 'klaednadur')
 * @returns {object|undefined} The category object, or undefined if not found.
 */
export function getCategoryById(id) {
  return categories.find((c) => c.id === id);
}
